                <div id= "box">
                    {/* <h4>{cat.name}</h4>
                    <small>Age: {cat.age}</small>
                    <small>Hobby: {cat.hobby}</small> */}
                </div>
                </ListGroup>

                <Button>Edit</Button>
                <Button>Delete</Button>

            </div>

            {/* <Switch>
                <Route exact path="/" />
            </Switch> */}
        {/* </Router>  */}
        </>
    )
    }
}

export default CatShow
