import React, { Component } from 'react'
import { Form, FormGroup, Label, Input, Button }
from 'reactstrap';
import cats from "../cats"
import {Link, Route, BrowserRouter as Router} from "react-router-dom"

class CatCreate extends Component {
    constructor(props) {
        super(props)
        this.state = {
          success: false,
          form:{
            name: '',
            age: '',
            hobby: ''
        }
      }
    }

    handleChange = (event) => {
      let { form } = this.state
      form[event.target.name] = event.target.value
      this.setState({ form: form })
    }

    handleSubmit = (e) => {
      e.preventDefault()
      // console.log(this.state.form);
      this.props.handleSubmit(this.state.form)
      this.setState({
      success: true
    })
    }

    render() {
    return (
    <>
      <a href ="/" id="button"><Button>Back Home</Button></a>
      <br/>
      <br/>
      <Form>
        <FormGroup id="form">
          <Label htmlFor="name" id="name">Name</Label>
          <Input
            type="text"
            name="name"
            onChange={ this.handleChange }
            value={ this.state.form.name }
          />
          <br/>
          <Label htmlFor="age" id="age">Age</Label>
          <Input
            type="text"
            name="age"
            onChange={ this.handleChange }
            value={ this.state.form.age }
          />
          <br/>
          <Label htmlFor="hobby" id="hobby">Hobby</Label>
          <Input
            type="text"
            name="hobby"
            onChange={ this.handleChange }
            value={ this.state.form.hobby }
          />
        </FormGroup>
        <Button type="submit" id="submit" onClick={ this.handleSubmit }>Add New Cat</Button>
      </Form>

    </>
    )}
}

export default CatCreate
