import React, { Component } from 'react'
import { ListGroup, ListGroupItemHeading, ListGroupItemText }
from 'reactstrap';
import cats from '../cats'


class CatShow extends Component {
    constructor(props) {
        super(props)
        this.state = {
          cats: cats
        }
      }

    render() {
        const {id} = this.props.match.params
        const cat = this.props.cats.find((cat) => cat.id === parseInt(id))
    return (
        <div>
            <ListGroup>
                <h4>{cat.name}</h4>
                <small>{cat.age}</small>
                <small>{cat.enjoys}</small>
            </ListGroup>
        </div>
    )
    }
}

export default CatShow
